use glium::texture::RawImage2d;

pub fn ascii_raw_img() -> RawImage2d<'static, u8> {
    let mut data: Vec<u8> = Vec::with_capacity(4 * 128 * 128);
    for row in ASCII_IMG.iter() {
        for ch in row.iter() {
            if *ch == b' ' {
                // Transparent
                data.push(0);
                data.push(0);
                data.push(0);
                data.push(0);
            } else {
                // White
                //data.push(0xFFFFFFFF);
                data.push(0xFF);
                data.push(0xFF);
                data.push(0xFF);
                data.push(0xFF);
            }
        }
    }
    RawImage2d::from_raw_rgba(data, (128, 128))
}

